def test_request_exception_signal():
    app = flask.Flask(__name__)
    recorded = []

    @app.route("/")
    def index():
        raise ZeroDivisionError

    def record(sender, exception):
        recorded.append(exception)

    flask.got_request_exception.connect(record, app)
    try:
        assert app.test_client().get("/").status_code == 500
        assert len(recorded) == 1
        assert isinstance(recorded[0], ZeroDivisionError)
    finally:
        flask.got_request_exception.disconnect(record, app)

def test_streaming_with_context_and_custom_close(self, app, client):
    called = []

    class Wrapper:
        def __init__(self, gen):
            self._gen = gen

        def __iter__(self):
            return self

        def close(self):
            called.append(42)

        def __next__(self):
            return next(self._gen)

        next = __next__

    @app.route("/")
    def index():
        def generate():
            yield "Hello "
            yield flask.request.args["name"]
            yield "!"

        return flask.Response(flask.stream_with_context(Wrapper(generate())))

    rv = client.get("/?name=World")
    assert rv.data == b"Hello World!"
    assert called == [42]

def test_appcontext_signals(app, client):
    recorded = []

    def record_push(sender, **kwargs):
        recorded.append("push")

    def record_pop(sender, **kwargs):
        recorded.append("pop")

    @app.route("/")
    def index():
        return "Hello"

    flask.appcontext_pushed.connect(record_push, app)
    flask.appcontext_popped.connect(record_pop, app)
    try:
        rv = client.get("/")
        assert rv.data == b"Hello"
        assert recorded == ["push", "pop"]
    finally:
        flask.appcontext_pushed.disconnect(record_push, app)
        flask.appcontext_popped.disconnect(record_pop, app)

